mapper.select:
select
SQRT(ABS(shop_lng - #{condition.clientLng, jdbcType=DECIMAL})*ABS(shop_lng - #{condition.clientLng, jdbcType=DECIMAL}) +ABS(shop_lat - #{condition.clientLat, jdbcType=DECIMAL})*ABS(shop_lat - #{condition.clientLat, jdbcType=DECIMAL})) AS s_distance,
from way_discount discount left join user_login login on login.id = discount.user_login_id left join way_discount_real discount_real on discount_real.discount_id = discount.id and discount_real.is_deleted=0 and discount_real.user_login_id = #{condition.realUserLoginId, jdbcType=BIGINT} where discount.is_deleted=0
and discount.limit_time_expire >= now()
and discount.id = #{condition.discountId,jdbcType=BIGINT}
and discount.city_code = #{condition.cityCode,jdbcType=VARCHAR}
order by s_distance
order by discount.limit_time_expire desc
limit ${pageable.offset}, ${pageable.pageSize}
