mapper.select:
select , commodity.name as 'commodityName', commodity.id as 'commodityId', commodity.price as 'commodityPrice', commodity.img_url as 'commodityImgUrl', sq.id as 'sq_id', sq.shop_id as 'sq_shop_id', sq.idcard_front_img_url, sq.idcard_back_img_url, sq.idcard_hand_img_url, sq.business_license_img_url, sq.shop_outside_img_url, sq.shop_inside_img_url, sq.other1_img_url, sq.other2_img_url, sq.other3_img_url, sq.other4_img_url, sq.other5_img_url from way_shop shop left join way_shop_cate_leaf leaf on shop.shop_cate_leaf_id = leaf.id left join way_shop_cate_root root on root.id = leaf.shop_cate_root_id left join way_commodity commodity on shop.id = commodity.shop_id and commodity.is_deleted = 0 left join way_shop_qualification sq on sq.shop_id = shop.id where shop.is_deleted != 1
and shop.id = #{condition.id, jdbcType=BIGINT}
and shop.shop_cate_leaf_id = #{condition.shopCateLeafId, jdbcType=INTEGER}
and shop.city_code = #{condition.cityCode,jdbcType=VARCHAR}
shop.shop_name like concat('%', #{condition.shopName, jdbcType=VARCHAR}, '%') or shop.shop_pinyin like concat('%', #{condition.shopName, jdbcType=VARCHAR}, '%') or shop.shop_py like concat('%', #{condition.shopName, jdbcType=VARCHAR}, '%') or
limit ${pageable.offset}, ${pageable.pageSize}
